mapper.update:
update pw_pesticide_inventory
set price=#{putStorage.price},
deposit=#{putStorage.deposit},
number=number + #{putStorage.number}
where basic_info_id = #{putStorage.basicInfoId}
and DEPT_ID = #{deptId}

update pw_pesticide_inventory
set number=number - #{number}
where ID = #{inventoryId}
and number >= #{number}
and DEPT_ID = #{deptId}
